import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPhotos();
  }, []);

  const fetchPhotos = async () => {
    try {
      const response = await axios.get('/api/photos');
      setPhotos(response.data);
    } catch (error) {
      setError('Sorry, we are facing some issues. Please try again later.');
      console.error('Error fetching photos:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="card">
        <h2>Loading Memes...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="error">{error}</div>
        <button onClick={fetchPhotos} className="btn btn-primary">
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div>
      <div className="card">
        <h1>Welcome to MemeStream! ðŸŽ­</h1>
        <p>Discover and vote on the funniest GIFs from around the web.</p>
      </div>

      {photos.length === 0 ? (
        <div className="card">
          <h2>No memes yet!</h2>
          <p>Be the first to upload a funny GIF!</p>
        </div>
      ) : (
        <div className="photo-grid">
          {photos.map((photo) => (
            <div key={photo.photo_id} className="photo-card">
              <Link to={`/photo/${photo.photo_id}`}>
                <img 
                  src={`/photos/${(photo.name || '').trim()}.gif`} 
                  alt={photo.name}
                  onError={(e) => {
                    e.target.src = 'data:image/svg+xml;base64,';
                  }}
                />
                <div className="photo-card-content">
                  <h3>{photo.name}</h3>
                  <p>Click to view and comment!</p>
                </div>
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
